package pl.michalwieczorek.restaurantservice.Controller;

import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ResponseBody;
import pl.michalwieczorek.restaurantservice.Model.Meal;

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

@Controller
public class HomeService {
    public static List<Meal> Meals = new ArrayList<>();
    static{
        Meals.add(new Meal("Kotlet Schabowy", "Delikatny kotlet schabowy panierowany w chrupiącej bułce tartej, podawany z miękkimi, gotowanymi ziemniakami. Do tego świeża surówka z marchewki, kapusty i ogórka z delikatnym dressingiem. Danie pełne tradycyjnych smaków, które rozpieści Twoje podniebienie.",
                "https://www.tasteatlas.com/images/dishes/7c83f24ab48449c2a6f70784285b8e3f.jpg", new BigDecimal(40)));
        Meals.add(new Meal("Spaghetti Carbonara", "Delikatne spaghetti al dente perfekcyjnie połączone z kremowym sosem carbonara, stworzonym z połączenia świeżych jajek, delikatnego sera parmezan, wędzonego guanciale oraz szczypty świeżo mielonego czarnego pieprzu. To tradycyjne włoskie danie z pewnością zaspokoi Twoje kulinarne pragnienia.",
                "data:image/jpeg;base64,", new BigDecimal(40)));
    }
    @GetMapping("/")
    public String HomePage(Model model){
        model.addAttribute( "meals", Meals);
        return "home";
    }
}
